			for(int k = 0; k < (1 << 16); ++k)
				if(!(schedule & k))
					chmax(dp[(schedule | k)], dp[k] + l);
		}

		cout << *max_element(dp.begin(), dp.end()) << endl;
	}

	return EXIT_SUCCESS;
}

