    rep(S, 1<<16) {
      rep(j, M) {
        if((S ^ v[j]) == (S + v[j])) {
          int nS = S ^ v[j];
          maximize(dp[(i+1)&1][nS], dp[i&1][S] + L);
        }
      }
    }
  }

  return *max_element(dp[N&1], dp[N&1]+(1<<16));
}

