#define MEMSET(v, h) memset((v), h, sizeof(v))

int n;
ll memo[110][1 << 16];
ll l[110];
int need[110];

ll calc(int depth, int use) {
  if (depth == n) { return 0; }
  if (memo[depth][use] != -1) { return memo[depth][use]; }
  ll ret = 0;
  ret = max(ret, calc(depth + 1, use));
  if ((use & need[depth]) == 0) {
    ret = max(ret, calc(depth + 1, use | need[depth]) + l[depth]);
  }
  return memo[depth][use] = ret;
}

