struct Guys{
  int L;
  vector<pair<int,int> > V;
};

bool Sc[24];
int N,M,s,e;
Guys Guy[100];
long long ans;

bool check(int a)
{
  for(int i=0;i<Guy[a].V.size();i++){
    for(int j=0;j<=23;j++){
      if(Guy[a].V[i].first <= j && j < Guy[a].V[i].second){
	if(Sc[j]==false)return false;
      }
    }
  }
  return true;
}

void fill(int a)
{
  for(int i=0;i<Guy[a].V.size();i++){
    for(int j=Guy[a].V[i].first;j<Guy[a].V[i].second;j++)Sc[j]=false;
  }
}

void erase(int a)
{
  for(int i=0;i<Guy[a].V.size();i++){
    for(int j=Guy[a].V[i].first;j<Guy[a].V[i].second;j++)Sc[j]=true;
  }
}

void dfs(int a,long long sum)
{
  if(a==N)return;
  if(!check(a)){
    for(int i=a+1;i<N;i++)dfs(i,sum);
  }
  else {
    sum+=Guy[a].L;
    ans=max(sum,ans);
    fill(a);
    for(int i=a+1;i<N;i++)dfs(i,sum);
    erase(a);
  }
}

int main()
{
  while(1){
    cin>>N;
    if(N==0)break;
    for(int i=0;i<100;i++){
      Guy[i].V.clear();
    }
    for(int i=0;i<N;i++){
      cin>>M>>Guy[i].L;
      for(int j=0;j<M;j++){
	cin>>s>>e;
        Guy[i].V.push_back(make_pair(s,e));
      }
    }
    for(int i=0;i<=23;i++)Sc[i]=true;
    ans=0;
    for(int i=0;i<N;i++)dfs(i,0);
    cout<<ans<<endl;
  }
  return 0;
}

