using namespace std;

int N;
vector<int> M, L;
vector<int> dating;

int main () {
    while (true) {
        cin >> N;
        if (N == 0) {
            break;
        }

        M.resize(N);
        L.resize(N);
        dating.resize(N);

        for (int j = 0; j < N; ++j) {
            cin >> M[j] >> L[j];
            dating[j] = 0;
            for (int k = 0; k < M[j]; ++k) {
                int a, b;
                cin >> a >> b;
                for (int t = a-6; t < b-6; ++t) {
                    dating[j] |= (1 << t);
                }
            }
        }

        vector<int> dp = vector<int>(1 << 16, 0);
        int ret = 0;

        for (int b = 1; b <= 16; ++b) {
            for (int j = 0; j < (1 << 16); ++j) {
                int bit_count = 0, t = j;
                while (t > 0) {
                    bit_count += t % 2;
                    t >>= 1;
                }
                if (bit_count != b) continue;

                for (int k = 0; k < 16; ++k) {
                    if (j & k) {
                        dp[j] = max(dp[j], dp[j ^ k]);
                    }
                }

                for (int k = 0; k < N; ++k) {
                    if ((j | dating[k]) == j) {
                        dp[j] = max(dp[j], dp[j ^ dating[k]] + L[k]);
                    }
                }

            }
        }

        cout << dp[(1 << 16) - 1] << endl;
    }
    return 0;
}

