			for(int j=0;j<(1<<17);j++){
				if(dp[i][j]>=0){
					dp[i+1][j]=max(dp[i+1][j],dp[i][j]);
					res=max(dp[i+1][j],res);
					bool flag=true;
					for(int k=0;k<m[i];k++){
						for(int l=s[i][k];l<e[i][k];l++){
							if(j>>l & 1)flag=false;
						}
					}
					if(flag){
						int nj=j;
						for(int k=0;k<m[i];k++){
							for(int l=s[i][k];l<e[i][k];l++){
								nj=nj| (1<<l);
							}
						}
						dp[i+1][nj]=max(dp[i+1][nj],dp[i][j]+l[i]);
						res=max(dp[i+1][nj],res);
					}
				}
			}
		}
		printf("%lld\n",res);
	}
	return 0;
}

