struct dpp{
	bitset<17-1> dates;
	int max;
	dpp(){
		dates.reset();
		max = 0;
	}
	bool isValid( vector<int>s, vector<int> e ) {
		bool ret = false;
		for( int i = 0; i < s.size(); i++ ) {
			for( int j = 0; j < e[i] - s[i]; j++ ) {
				ret |= dates[ j + s[i] - 6 ];
			}
			if( ret ) break;
		}
		return !ret;
	}
	void set( vector<int> s, vector<int> e ){
		for( int i = 0; i < s.size(); i++ ) {
			for( int j = 0; j < e[i] - s[i]; j++ ) {
				dates.set( j + s[i] - 6 );
			}
		}
	}
};

unsigned long long int max( unsigned long long int p, unsigned long long int q ) {
	if( p > q ) return p;
	return q;
}

int main(void){

	while( true ) {
		int N;
		vector< guy > guys;

		/*
			dp[最後に選んだインデックス][選んだ人数].dates = スケジュール
			dp[最後に選んだインデックス][選んだ人数].max = 効用最大値
		*/
		dpp dp[100][16];

		unsigned long long int ans = 0;
		cin >> N;
		if( N == 0 ) break;

		for( int i = 0; i < N; i++ ) {
			guy g;
			cin >> g.M >> g.L;
			for( int j = 0; j < g.M; j++) {
				int tmp, tmp2;
				cin >> tmp >> tmp2;
				g.S.push_back( tmp );
				g.E.push_back( tmp2 );
			}
			guys.push_back( g );
		}

		//dpテーブルの初期値を設定
		for( int i = 0; i < N; i++ ) {
			dp[i][0].set( guys[i].S , guys[i].E );
			dp[i][0].max = guys[i].L;
		}

		//dpをまわす
		for( int i = 0; i < N; i++ )
			for( int j = 0; j < i; j++) 
				for( int k = 1; k < 16; k++) 
					if( dp[j][k - 1].isValid( guys[i].S, guys[i].E ) ) {
						dp[i][k].max = max( dp[j][k-1].max + guys[i].L , dp[i][k].max );
						dp[i][k].dates = dp[j][k-1].dates;
						dp[i][k].set( guys[i].S, guys[i].E );
					}

		for( int i = 0; i < N; i++ )
			for( int k = 0; k < 16; k++)
				ans = max( ans, dp[i][k].max );


		cout << ans << endl;
	}

	return 0;
}

