#include <bits/stdc++.h>
using namespace std;
const int MAX_N=110,MAX_M=17,MAX_L=17;

int N;
int M[MAX_N],L[MAX_N],S[MAX_N][MAX_M],E[MAX_N][MAX_M];
int dp[MAX_N][1<<(MAX_L)];

void solve(){
    for (int i=0;i<=N;++i)
        for (int mask=0;mask<(1<<MAX_L);++mask)
            dp[i][mask]=-1;
    dp[0][0]=0;
    for (int i=0;i<N;++i){
        for (int mask=0;mask<(1<<MAX_L);++mask){
            if (dp[i][mask]==-1) continue;
            dp[i+1][mask]=max(dp[i+1][mask],dp[i][mask]);
            int ok=1,nxt=mask;
            for (int j=0;j<M[i];++j){
                for (int k=S[i][j];k<E[i][j];++k){
                    if (mask&1<<k) ok=0;
                    nxt|=1<<k;
                }
            }
            if (ok) dp[i+1][nxt]=max(dp[i+1][nxt],dp[i][mask]+L[i]);
        }
    }
    int ans=0;
    for (int mask=0;mask<(1<<MAX_L);++mask) ans=max(ans,dp[N][mask]);
    cout << ans << '\n';
}

int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);
    while(cin >> N,N){
        for (int i=0;i<N;++i){
            cin >> M[i] >> L[i];
            for (int j=0;j<M[i];++j){
                cin >> S[i][j] >> E[i][j];
                S[i][j]-=6,E[i][j]-=6;
            }
        }
        solve();
    }
}

