/*
  最悪ケースはmax(n)(=100)人についてmax(m)(=16)+1通りずつ見る時
  bitset::any はO(bitset.size())
  bitset::operator& は

  n: 男性の人数
  k: 今見ている男性
  guy: guy[i][j] = i番目の男性についてのj番目のデート候補
  cost: cost[i] = i番目の男性の満足度
  sum: 一日デートしたときの満足度の総和
  bs: 一日の予定
 */
ll solve(int n, int k, const vector<vector<pair<int, int>>> &guy, const vector<ll> &cost, ll sum, ll bs) {
  if(n == k) {
    return sum;
  } else {
    int sz = guy[k].size();
    ll res = solve(n, k+1, guy, cost, sum, bs);
    for(int i=0;i<sz;++i) {
      int s = guy[k][i].first, e = guy[k][i].second;
      ll tmp = ((1ll<<e)-1) - ((1ll<<s)-1);
      // すでにある予定と[s, e)にカブりがあれば実行不可能
      // そうでなければsumにcost[k]を足して、スケジュールに[s, e)を追加
      if((bs & tmp) != 0) {
        // cout << k << ":" << i << ":" << bs << ":" << tmp << endl;
        return 0;
      } else {
        res = max(res, solve(n, k+1, guy, cost, sum+cost[k], bs|tmp));
      }
    }
    return res;
  }
}


int main () {
  cin.tie(0);
  cout << fixed << setprecision(10);

  while(1) {
    int n; cin >> n;
    if(n == 0) break;
    vector<vector<pair<int, int>>> guy(n); // i番目とのデート時間候補
    vector<ll> cost(n); // i番目のコスト
    for(int i=0;i<n;++i) {
      int m; ll l; cin >> m >> l;
      cost[i] = l;
      for(int j=0;j<m;++j) {
        int s, e; cin >> s >> e;
        guy[i].push_back(make_pair(s, e));
      }
    }

    ll sum = 0;
    ll bs = 0;
    cout << solve(n, 0, guy, cost, sum, bs) << endl;
  }
}

