#include <vector>
#include <iostream>
#include <algorithm>
using namespace std;
struct Man {
	int l;
	vector<int> bit;
};
int N, M, a, b;
int main() {
	while (cin >> N, N) {
		vector<Man> D(N);
		for (int i = 0; i < N; i++) {
			cin >> M >> D[i].l;
			D[i].bit.resize(M);
			for (int j = 0; j < M; j++) {
				cin >> a >> b;
				for (int k = a - 6; k < b - 6; k++) D[i].bit[j] += (1 << k);
			}
		}
		vector<vector<int> > dp(N + 1, vector<int>(1 << 16));
		for (int i = 1; i <= N; i++) {
			for (int j = 1; j < 1 << 16; j++) {
				dp[i][j] = dp[i - 1][j];
				for (int k : D[i - 1].bit) {
					if (~j & k) continue;
					dp[i][j] = max(dp[i][j], dp[i - 1][j - k] + D[i - 1].l);
				}
			}
		}
		cout << dp[N][(1 << 16) - 1] << endl;
	}
	return 0;
}

