using namespace std;

int bfs(int n, vector<set<int> > vs, vector<int> val,  set<int> s, int sum, int node) {
    if (node == n) return sum;

    set<int> a, b;
    a = s;
    b = vs[node];
    a.insert(b.begin(), b.end());
    if (a.size() == s.size() + b.size()) {
        return max(bfs(n, vs, val, s, sum, node+1), bfs(n, vs, val, a, sum+val[node], node+1));
    }
    else return bfs(n, vs, val, s, sum, node+1);
}

int main(void){
    int n;
    while (cin>>n && n) {
        vector<set<int> > vs(n);
        vector<int> val(n);
        for (int i = 0; i < n; i++) {
            int m;
            cin >> m >> val[i];
            for (int j = 0; j < m; j++) {
                int a, b;
                cin >> a >> b;
                for (int k = a; k < b; k++) vs[i].insert(k);
            }
        }
        set<int> s;
        int ans = bfs(n, vs, val, s, 0, 0);
        cout << ans << endl;
    }
}

