const int INF = 1 << 22;

const int MAX_N = 100;
const int MAX_M = 16;
const int WIDTH = 16;

int happy[MAX_N];
int schedule[MAX_N];

int dp[2][1 << WIDTH];

int N, M;

int make_mask(int s, int e) {
    s -= 6; e -= 6;
    int w = 1, res = 0;
    REP (bit, WIDTH) {
        if (bit >= s && bit < e) res += w;
        w *= 2;
    }
    return res;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    while (cin >> N, N) {
        fill((int* )dp, (int* )(dp + 2), -INF);

        REP (i, N) {
            cin >> M >> happy[i];
            int mask = 0;
            REP (j, M) {
                int s, e;
                cin >> s >> e;
                mask |= make_mask(s, e);
            }
            schedule[i] = mask;
        }

        dp[0][0] = 0;
        REP (i, N) {
            REP (j, 1 << WIDTH) {
                // スケジュールを選べるのなら
                if ((j & schedule[i]) == 0) {
                    dp[(i+1)%2][j|schedule[i]] = max(dp[(i+1)%2][j|schedule[i]], dp[i%2][j]+happy[i]);
                }
                dp[(i+1)%2][j] = max(dp[(i+1)%2][j], dp[i%2][j]);
            }
        }

        int ans = 0;
        REP (i, 1 << WIDTH) {
            ans = max(ans, dp[N%2][i]);
        }
        cout << ans << endl;
    }
    return 0;
}

