#include <iostream>
#include <vector>
#include <algorithm>
#include <cstring>

using namespace std;

typedef pair<int,int> pii;

struct Guy{
	int sc;
	int L;
	bool ok;
};

typedef long long ll;
int n;
Guy guys[101];

ll dp[101][1<<17];

ll dfs(int pos,int s){
	if(pos==n)return 0;
	if(dp[pos][s]!=-1)return dp[pos][s];
	ll res=0;
	// ツ債。ツ嘉アツ偲ヲツづゥツつゥツ氾崢つゥ
	// no get
	res=max(res,dfs(pos+1,s));
	// get
	if(guys[pos].ok){
		bool ok=true;
		for(int i=0;i<17;i++){
			if(((s>>i)&1)&&((guys[pos].sc>>i)&1)){
				ok=false;
				break;
			}
		}
		if(ok){
			res=max(res,dfs(pos+1,s|guys[pos].sc)+guys[pos].L);
		}
	}
	return dp[pos][s]=res;
}

int main(){
	while(cin>>n&&n!=0){
		for(int i=0;i<n;i++){
			int m;
			cin>>m>>guys[i].L;
			guys[i].sc=0;
			guys[i].ok=true;
			for(int j=0;j<m;j++){
				int s,e;
				cin>>s>>e;
				s-=6;
				e-=6;
				for(int k=s;k<e;k++){
					if(!((guys[i].sc>>k)&1)){
						guys[i].sc|=(1<<k);
					}
					else{
						guys[i].ok=false;
					}
				}
			}
		}
		for(int i=0;i<101;i++)for(int j=0;j<(1<<17);j++)dp[i][j]=-1;
		ll res=dfs(0,0);
		cout<<res<<endl;
	}

	return 0;
}

