ll solve(int n, int k, const vector<vector<pair<int, int>>> &guy, const vector<ll> &cost, vector<int> &v) {
  if(n == k) {
    ll res = 0;
    vector<bool> sel(23);
    for(int i=0;i<n;++i) {
      int m = v[i];
      if(m != -1) {
        int s = guy[i][m].first, e = guy[i][m].second;
        for(int j=s;j<e;++j) {
          if(sel[j]) return 0;
          else sel[j] = true;
        }
        res += cost[i];
      }
    }
    return res;
  } else {
    int sz = guy[k].size();
    ll res = solve(n, k+1, guy, cost, v);
    for(int i=0;i<sz;++i) {
      v[k] = i;
      res = max(res, solve(n, k+1, guy, cost, v));
    }
    return res;
  }
}

int main () {
  cin.tie(0);
  cout << fixed << setprecision(10);

  while(1) {
    int n; cin >> n;
    if(n == 0) break;
    vector<vector<pair<int, int>>> guy(n);
    vector<ll> cost(n);
    for(int i=0;i<n;++i) {
      int m; ll l; cin >> m >> l;
      cost[i] = l;
      for(int j=0;j<m;++j) {
        int s, e; cin >> s >> e;
        guy[i].push_back(make_pair(s, e));
      }
    }

    vector<int> v(n, -1);
    cout << solve(n, 0, guy, cost, v) << endl;;
  }
}

