#include<iostream>
#include<vector>
using namespace std;
typedef long long ll;

const ll INF = 1LL << 40;

void solve(int n) {
    vector<ll> L(n);
    vector<vector<int> > S(n), E(n);
    int M;
    for (int i = 0; i < n; ++i) {
        cin >> M >> L[i];
        int s, e;
        for (int j = 0; j < M; ++j) {
            cin >> s >> e;
            s -= 6, e -= 6;
            S[i].push_back(s);
            E[i].push_back(e);
        }
    }
    vector<vector<ll> > dp(n + 1, vector<ll>(1 << 16, -INF));
    for (int i = 0; i <= n; ++i) dp[i][0] = 0;
    for (int i = 1; i <= n; ++i) {
        for (int mask = 0; mask < 1 << 16; ++mask) {
            dp[i][mask] = dp[i - 1][mask];
            bool is_ok = true;
            for (int j = 0; j < S[i - 1].size(); ++j) {
                for (int k = S[i - 1][j]; k < E[i - 1][j]; ++k) {
                    if (((mask >> k) & 1) == 0) {
                        is_ok = false;
                        break;
                    }
                }
                if (!is_ok) break;
            }
            if (!is_ok) continue;
            int prev_mask = mask;
            for (int j = 0; j < S[i - 1].size(); ++j) {
                for (int k = S[i - 1][j]; k < E[i - 1][j]; ++k) {
                    prev_mask ^= 1 << k;
                }
            }
            dp[i][mask] = max(dp[i][mask], dp[i - 1][prev_mask] + L[i - 1]);
        }
    }
    ll ans = -INF;
    for (int mask = 0; mask < 1 << 16; ++mask) {
        ans = max(ans, dp[n][mask]);
    }
    cout << ans << endl;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);
    int N;
    while (true) {
        cin >> N;
        if (N == 0) break;
        solve(N);
    }
    return 0;
}

