            u[i].first = s;
        }
        for (int i = 0; i < (1<<16); ++i) {
            for (int j = 0; j < n; ++j) {
                if((i & u[j].first) == u[j].first){
                    dp[i] = max(dp[i], dp[i^u[j].first]+u[j].second);
                }
            }
        }
        cout << *max_element(dp.begin(),dp.end()) << "\n";
    }
    return 0;
}

