int dp[2][(1 << 18)];

signed main(){

    while(1){
        int n; cin >> n;
        if(!n) break;

        memset(dp, -1, sizeof(dp));
        dp[0][0] = 0;

        int cur = 0;
        int nxt = 1;
        for(int i = 0; i < n; i++){
            int m, l; cin >> m >> l;
            vector<int> s(m);
            vector<int> e(m);
            for(int j = 0; j < m; j++){ 
                cin >> s[j] >> e[j];
                s[j] -= 6;
                e[j] -= 6;
            }
            for(int bit = 0; bit < (1 << 18); bit++){
                if(dp[cur][bit] == -1) continue;

                //デートしない
                dp[nxt][bit] = max(dp[nxt][bit], dp[cur][bit]);


                //デートする
                bool ok = true;
                int nxtbit = 0;
                for(int j = 0; j < m; j++){
                    for(int k = s[j]; k < e[j]; k++) nxtbit = nxtbit | (1 << k);       
                }

                for(int k = 0; k < 18; k++) if(nxtbit & (1 << k) && bit & (1 << k)) ok = false;
                nxtbit = bit | nxtbit;
                if(ok) dp[nxt][nxtbit] = max(dp[nxt][nxtbit], dp[cur][bit] + l);
            }

            swap(cur, nxt);
        }

        int ans = -1;
        for(int j = 0; j < (1 << 18); j++) ans = max(ans, dp[cur][j]);
        cout << ans << endl;
    }
    return 0;
}

