					date|=(1<<k);
			}
			for(int j=0;j<(1<<17);j++)
			{
				if((j&date)==date)
					dp[i][j]=max(dp[i-1][j],dp[i-1][j^date]+l[i-1]);
				else
					dp[i][j]=dp[i-1][j];
			}
		}
		//end dp
		ans=0;
		for(int i=0;i<(1<<17);i++)
			ans=max(ans,dp[n][i]);
		cout<<ans<<endl;
	}
	return 0;
}

