					for(int k = s; k < t; k++){
						nowbit= (nowbit | (1 << k));
					}
				}
				bitlist[i] = nowbit;
			}
			int len = (1 << (22 - 6));
			long [] dp = new long[len];
			long max = 0;
			for(int i = 0; i < n; i++){
				for(int j = 0; j < len; j++){
					if((bitlist[i] & j) == 0){
						int next = bitlist[i] | j;
						long value = dp[j] + llist[i];
						dp[next] = Math.max(dp[next], value);
						max = Math.max(max, value);
					}
				}
			}

			System.out.println(max);
		}
	}

