class Main{

    void solve(){
        Scanner sc = new Scanner(System.in);

        while(true){
            int n = sc.nextInt();
            if(n==0) break;

            int[][] dp = new int[2][1<<17];

            int idx = 0;
            for(int i=0; i<n; i++){
                int m = sc.nextInt();
                int L = sc.nextInt();
                for(int j=0; j<m; j++){
                    int s = sc.nextInt()-6;
                    int e = sc.nextInt()-6;
                    int bit = 0;
                    for(int k=s; k<=e; k++) bit ^= (1<<k);
                    for(int k=0; k<(1<<17); k++){

                        boolean boo = true;
                        for(int l=s+1; l<e; l++){
                          if((k&(1<<l))>0){
                              boo = false;
                              break;
                          }
                        }
                        if(!boo) continue;

                        int kk = k;
                        for(int l=s+1; l<e; l++){
                            if((kk^(1<<l))>0) kk ^= (1<<l);
                        }

                        if(k!=0 && dp[1-idx][k]==0) continue;
                        dp[idx][k|bit] = Math.max(dp[idx][k], L + dp[1-idx][k]);

                    }
                }
                idx = 1-idx;
            }

            idx = 1-idx;
            int max = 0;
            for(int i=0; i<(1<<17); i++){
                max = Math.max(max, dp[idx][i]);
            }
            System.out.println(max);
        }
    }

    public static void main(String[] args){
        new Main().solve();
    }
}

