import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.Scanner;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        Scanner in = new Scanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        Butterfly solver = new Butterfly();
        try {
            int testNumber = 1;
            while (true)
                solver.solve(testNumber++, in, out);
        } catch (UnknownError e) {
            out.close();
        }
    }

    static class Butterfly {
        public void solve(int testNumber, Scanner in, PrintWriter out) {
            final int MAX = 1 << 17;
            int N = in.nextInt();
            if (N == 0) throw new UnknownError();
            long[] dp = new long[MAX];
            long[] _dp = new long[MAX];
            for (int i = 0; i < N; i++) {
                int M = in.nextInt();
                long L = in.nextLong();
                int mask = 0;
                for (int j = 0; j < M; j++) {
                    int s = in.nextInt() - 6;
                    int e = in.nextInt() - 6;
                    for (int bit = s; bit < e; bit++) {
                        mask |= (1 << bit);
                    }
                }
                for (int bit = 0; bit < MAX; bit++) {
                    if ((bit & mask) == 0) {
                        dp[bit | mask] = Math.max(dp[bit | mask], _dp[bit] + L);
                    } else {
                        dp[bit | mask] = Math.max(dp[bit | mask], _dp[bit]);
                    }
                }
                _dp = Arrays.copyOf(dp, MAX);
            }
            long ans = 0;
            for (int i = 0; i < MAX; i++) {
                ans = Math.max(ans, _dp[i]);
            }
            out.println(ans);
        }

    }
}

import java.util.Scanner;

public class Main {

	static Scanner sc = new Scanner(System.in);
	static int N, ans;
	static int[] L, times;

	public static void main(String[] args) {
		while (true) {
			N = sc.nextInt();
			if (N == 0) break;
			times = new int[N];
			L = new int[N];
			for (int i = 0; i < N; ++i) {
				int M = sc.nextInt();
				L[i] = sc.nextInt();
				for (int j = 0; j < M; ++j) {
					int S = sc.nextInt();
					int E = sc.nextInt();
					for (int k = S; k < E; ++k) {
						times[i] |= (1 << k);
					}
				}
			}
			ans = 0;
			dfs(0, 0, 0);
			System.out.println(ans);
		}
	}

	static void dfs(int cur, int st, int sum) {
		if (cur == N) {
			ans = Math.max(ans, sum);
			return;
		}
		if ((st & times[cur]) == 0) {
			dfs(cur + 1, st | times[cur], sum + L[cur]);
		}
		dfs(cur + 1, st, sum);
	}

}

