	    r=(r+t[i+j*2+k*3+l*4]*1LL*s[2]%M*rp[v.size()-1-i-j-k-l]%M*fact[i+j+l+k]%M*C[nd[1]][i]%M*C[nd[2]-1][j]%M*C[nd[3]][k]%M*C[nd[4]][l]%M)%M;
	  }	    
	  if(k!=nd[3]){
	    r=(r+t[i+j*2+k*3+l*4]*1LL*s[3]%M*rp[v.size()-1-i-j-k-l]%M*fact[i+j+l+k]%M*C[nd[1]][i]%M*C[nd[2]][j]%M*C[nd[3]-1][k]%M*C[nd[4]][l]%M)%M;
	  }	    
	  if(l!=nd[4]){
	    r=(r+t[i+j*2+k*3+l*4]*1LL*s[4]%M*rp[v.size()-1-i-j-k-l]%M*fact[i+j+l+k]%M*C[nd[1]][i]%M*C[nd[2]][j]%M*C[nd[3]][k]%M*C[nd[4]-1][l]%M)%M;
	  }
	  int d=dp[i][j][k][l]*1LL*(i+j+k+l)%M;
	  dp[i+1][j][k][l]=(dp[i+1][j][k][l]+d)%M;
	  dp[i][j+1][k][l]=(dp[i][j+1][k][l]+d)%M;
