typedef long long ll;
ll mod=1e9+7;

ll add(ll a,ll b){ return (a+b)%mod; }

void Add(int &a,ll b){
  ll tmp=(a+b)%mod;
  a=tmp;
}

ll mul(ll a,ll b){ return (a*b)%mod; }


ll mpow(ll a,ll b){
  ll res=1;
  while(b){
    if(b%2==1)res=mul(res,a);
    a=mul(a,a);
    b/=2;
  }
  return res;
}

ll divi( ll a,ll b){
  return mul(a, mpow(b,mod-2) );
}



