void update(int a,int b,int c,int d,int z){

  int na=a%2;
  int ma=1-na;

  /*  cout<<a<<' '<<b<<' '<<c<<' '<<d<<' '<<z<<endl;
  cout<<dp[a][b][c][d][z]<<endl;
  cout<<DP[a][b][c][d][z]<<endl;
  cout<<endl;    */
  if(a<cnt[1]){
    int cc=cnt[1]-a;
    ll pp=mul(dp[na][b][c][d][z], cc);
    Add( dp[ma][b][c][d][z], pp );
    Add( DP[ma][b][c][d][z], mul(DP[na][b][c][d][z], 10*cc )    );
    Add( DP[ma][b][c][d][z], mul( mul(pp,cntinv[1]),sum[1]) );
  }

  if(b<cnt[2]){
    int cc=cnt[2]-b;
    ll pp=mul(dp[na][b][c][d][z], cc);
    Add( dp[na][b+1][c][d][z], pp );
    Add( DP[na][b+1][c][d][z], mul(DP[na][b][c][d][z], 100*cc )    );
    Add( DP[na][b+1][c][d][z], mul(mul(pp,cntinv[2]),sum[2]) );
  }
  if(c<cnt[3]){
    int cc=cnt[3]-c;
    ll pp=mul(dp[na][b][c][d][z], cc);
    Add( dp[na][b][c+1][d][z], mul(dp[na][b][c][d][z], cc) );
    Add( DP[na][b][c+1][d][z], mul(DP[na][b][c][d][z], 1000*cc )    );
    Add( DP[na][b][c+1][d][z], mul(mul(pp,cntinv[3]),sum[3]) );
  }
  if(d<cnt[4]){
    int cc=cnt[4]-d;
    ll pp=mul(dp[na][b][c][d][z], cc);
    Add( dp[na][b][c][d+1][z], mul(dp[na][b][c][d][z], cc) );
    Add( DP[na][b][c][d+1][z], mul(DP[na][b][c][d][z], 10000*cc )    );
    Add( DP[na][b][c][d+1][z], mul(mul(pp,cntinv[4]),sum[4]) );
  }

  if(z<Z && a+b+c+d>0){
    Add( dp[na][b][c][d][z+1], dp[na][b][c][d][z] );
    Add( DP[na][b][c][d][z+1], mul(DP[na][b][c][d][z], 10 )    );
  }
}

ll solve(){
  dp.resize(2);
  for(int i=0;i<(int)dp.size();i++){
    dp[i].resize(cnt[2]+1);
    for(int j=0;j<(int)dp[i].size();j++){
      dp[i][j].resize(cnt[3]+1);
      for(int k=0;k<(int)dp[i][j].size();k++){
        dp[i][j][k].resize(cnt[4]+1);
        for(int l=0;l<(int)dp[i][j][k].size();l++){
          dp[i][j][k][l].resize(2);
        }
      }
    }
  }
  DP=dp;

  dp[0][0][0][0][0]=1;

  int ans=0;

  for(int a=0;a<=cnt[1];a++){
    for(int b=0;b<=cnt[2];b++)
      for(int c=0;c<=cnt[3];c++)
        for(int d=0;d<=cnt[4];d++)
          for(int z=0;z<=Z;z++){
            update(a,b,c,d,z);
            Add( ans, DP[a%2][b][c][d][z] );

            dp[a%2][b][c][d][z]=0;
            DP[a%2][b][c][d][z]=0;
          }
  }

  return ans;
}

int countDisit(int x){
  int res=0;
  while(x){
    res++;
    x/=10;
  }
  return res;
}

int main(){
