      Z++;
      continue;
    }
    cnt[D]++;
    sum[D]+=a[i];
  }

  for(int i=1;i<=4;i++)
    if(cnt[i]>0)
      cntinv[i]=divi(1,cnt[i]);
  cout<< solve() <<endl;

  return 0;
}

