}
int solve(vector<int> a) {
    int n = a.size();
    vector<int> d(n);
    REP(i, n) d[i] = to_string(a[i]).size();

    int ans = 0;

    const int P = 4 * n + 1;
    int ways[802][202] = {};
    ways[0][0] = 1;
    REP(i, n){
        int nways[802][202] = {};
        REP(l, P) REP(k, i+1) {
            add(nways[l][k], ways[l][k]);
            if(l + d[i] < P) add(nways[l+d[i]][k+1], ways[l][k]);
        }
        memcpy(ways, nways, sizeof(ways));
    }
    // REP(l, P) REP(k, n+1) if(ways[l][k] > 0) {
    //     cout << "length: " << l << endl;
    //     cout << "count: " << k << endl;
    //     cout << "ways: " << ways[l][k] << endl;
    // }

    REP(i, n){
        int base10 = 1;
        const int P = 4*(n-1) + 1;

        int dp[802][202] = {};
        REP(l, P) REP(k, n) {
            dp[l][k] = ways[l][k];
            if(l-d[i]>=0 && k-1 >= 0) {
                dp[l][k] -= dp[l-d[i]][k-1];
                if(dp[l][k] < 0) dp[l][k] += MOD;
            }
            // if(dp[l][k] > 0) {
            //     cout << "not use: " << i << endl;
            //     cout << "length: " << l << endl;
            //     cout << "count: " << k << endl;
            //     cout << "ways: " << dp[l][k] << endl;
            // }
        }
        REP(l, P) REP(k, n) {
            REP(j, k) dp[l][k] = (LL)(j+1) * dp[l][k] % MOD;
        }


        int dp2[802] = {};
        REP(k, n) {
            int rest = (n-1) - k;
            LL K = 1;
            REP(i, rest+1) {
                REP(l, P) add(dp2[l], K * dp[l][k] % MOD);
                K = K * (rest - i) % MOD;
            }
        }

        REP(p, P) {
            // if(dp2[p] > 0) {
            //     cout << "i: " << i << endl;
            //     cout << "10^" << p << endl;
            //     cout << "dp: " << dp2[p] << endl;
            // }

            add(ans, (LL)dp2[p] * base10 % MOD * a[i] % MOD);
            base10 = 10LL * base10 % MOD;
        }
    }
    return ans;
}
int main(){
    iostream_init();
