  vector<int> v;
  init();
  cin >> n;
  rep(i, n){
    cin >> a;
    v.push_back(a);
  }
  sort(v.begin(), v.end());
  res = calc(v);
  if(v[0] == 0) res = (res + mod - calc(vector<int>(v.begin() + 1, v.end()))) % mod;
  cout << res << endl;
  return 0;
}

