// ------ 0. Variables, Functions ------ //

int n;

vector<int> fact;
vector<int> power2;
vector<int> power10;

int digit(int n)
{
	if (n < 10) return 1;

	return digit(n / 10) + 1;
}

int solve(vector<int> a)
{
	vector<vector<int> > d(5, vector<int>(2, 0));

	for (int i = 0; i < n; i++)
	{
		d[digit(a[i])][0] += 1;
		d[digit(a[i])][1] += a[i];
	}

	int ret = 0;

	for (int i = 1; i <= 4; i++)
	{
		vector<vector<vector<int> > > dp(5, vector<vector<int> >(n, vector<int>(n * 4, 0)));

		dp[0][0][0] = 1;

		if (d[i][0] != 0)
		{
			d[i][0]--;

			for (int j = 0; j < 4; j++)
			{
				for (int k = 0; k < n; k++)
				{
					for (int l = 0; l < n * 4; l++)
					{
						if (dp[j][k][l] != 0)
						{
							for (int m = 0; m <= d[j + 1][0]; m++)
							{
								dp[j + 1][k + m][l + (j + 1) * m]++;
								dp[j + 1][k + m][l + (j + 1) * m] %= mod;
							}
						}
					}
				}
			}

			d[i][0]++;

			int res = 0;

			for (int j = 0; j < n; j++)
			{
				for (int k = 0; k < n * 4; k++)
				{
					res = (res + 1LL * dp[4][j][k] * fact[j] % mod * power10[k] % mod * power2[n - j - 1] % mod) % mod;
				}
			}

			ret = (ret + 1LL * res * d[i][1]) % mod;
		}
	}

	return ret;
}

