using namespace std;

int M=1000000007;

int f(vector<int> v,int s){
  int dp[823][223]={};
  dp[0][0]=1;
  for(int i=0;i<4;i++){
    for(int j=0;j<v[i];j++){
      for(int k=800;k>=i+1;k--){
	for(int l=200;l>0;l--){
	  dp[k][l]=(dp[k][l]+dp[k-(i+1)][l-1]*1LL*l)%M;
	}
      }
    }
  }
  int head[223]={1};
  for(int i=1;i<=200;i++){
    head[i]=(head[i-1]*1LL*i+1)%M;
  }
  int ans=0;
  int n=v[0]+v[1]+v[2]+v[3];
  int t=1;
  for(int i=0;i<=800;i++,t=(t*10)%M){
    for(int j=0;j<=n;j++){
      ans=(ans+dp[i][j]*1LL*head[n-j]%M*s%M*t%M)%M;
    }
  }
  return ans;
}

int solve(vector<int> a){
  int nd[4]={};
  int ds[4]={};
  for(auto e:a){
    int x=(e>=10)+(e>=100)+(e>=1000);
    nd[x]++;
    ds[x]=(ds[x]+e)%M;
  }
  int r=0;
  r=(r+(nd[0]?f({nd[0]-1,nd[1],nd[2],nd[3]},ds[0]):0))%M;
  r=(r+(nd[1]?f({nd[0],nd[1]-1,nd[2],nd[3]},ds[1]):0))%M;
  r=(r+(nd[2]?f({nd[0],nd[1],nd[2]-1,nd[3]},ds[2]):0))%M;
  r=(r+(nd[3]?f({nd[0],nd[1],nd[2],nd[3]-1},ds[3]):0))%M;
  return r;
}

int main(){
  int n;
  cin>>n;
  vector<int> a(n);
  vector<int> nz;
  for(int i=0;i<n;i++){
    cin>>a[i];
    if(a[i]){
      nz.push_back(a[i]);
    }
  }
  int ans=solve(a);
  if(nz.size()<n){
    ans=(ans-solve(nz)+M)%M;
  }
  cout<<ans<<endl;
}

