	for(int i=0;i<n;i++)
	{
		dig[i]=cnt_digit(a[i]);
		digit_sum[dig[i]]+=a[i];
		del[dig[i]]=i;
		if(a[i]==0)zero=i;
	}
	ll ans = solve();
	if(zero!=-1)
	{
		swap(a[zero],a[n-1]);
		N--;
		memset(del,-1,sizeof(del));
