			dig[i]=cnt_digit(a[i]);
			digit_sum[dig[i]]+=a[i];
			del[dig[i]]=i;
		}
		ans -= solve();
		ans = ((ans%MOD)+MOD)%MOD;
	}
	printf("%lld\n",ans);
	return 0;
}

