int ncr(int n,int r)
{
	static int memo[300][300];
	if(memo[n][r]) return memo[n][r];
	if(r==0 || r==n) return 1;
	return memo[n][r]=(ncr(n-1,r-1)+ncr(n-1,r))%MOD;
}

int solve(vi as)
{
	vi tinvs(1000,1); // tinvs[i]=10^i mod m
	repi(i,1,tinvs.size()) tinvs[i]=(ll)tinvs[i-1]*10%MOD;
	vi finvs(200,1); // finvs[i]=i! mod m
	repi(i,1,finvs.size()) finvs[i]=(ll)finvs[i-1]*i%MOD;

	int n=as.size();
	vi ls(n);
	rep(i,n){
		if(as[i]==0) ls[i]=1;
		for(int x=as[i];x;x/=10) ls[i]++;
	}
	array<int,5> hist={{}};
	rep(i,n) hist[ls[i]]++;

	ll res=0;
	rep(c1,hist[1]+1) rep(c2,hist[2]+1) rep(c3,hist[3]+1) rep(c4,hist[4]+1){
		array<int,5> uses={-1,c1,c2,c3,c4};
		int locnt=0,lolen=0;
		repi(len,1,5) locnt+=uses[len],lolen+=len*uses[len];
		repi(len,1,5) if(uses[len]<hist[len]){
			hist[len]--;
			ll lopat=finvs[c1+c2+c3+c4];
			repi(j,1,5) (lopat*=ncr(hist[j],uses[j]))%=MOD;
			hist[len]++;
			ll hipat=0;
			rep(j,n-locnt) (hipat+=(ll)ncr(n-locnt-1,j)*finvs[j]%MOD)%=MOD;
			rep(j,n) if(ls[j]==len){
				ll val=(ll)as[j]*tinvs[lolen]%MOD*lopat%MOD*hipat%MOD;
				(res+=val)%=MOD;
			}
		}
	}
	return res;
}

int main()
{
	for(int n;cin>>n && n;){
		vi as(n);
		rep(i,n) cin>>as[i];

		ll res=solve(as);
		if(count(all(as),0)){
			as.erase(find(all(as),0));
			ll tmp=solve(as);
			res=(res-tmp+MOD)%MOD;
		}
		cout<<res<<endl;
	}
}

