    finv[0] = finv[1] = 1;
    inv[1] = 1;
    for(int i=2;i<805;i++){
        inv[i] = MOD - (ll)inv[MOD%i] * (MOD/i) % MOD;
        fac[i] = (ll)fac[i-1] * i % MOD;
        finv[i] = (ll)finv[i-1] * inv[i] % MOD;
    }
}

int comb(int a, int b)
{
    if(a < b){
        return 0;
    }else{
        return fac[a] * ((ll)finv[b] * finv[a-b] % MOD) % MOD;
    }
}

