            for(int j = 0; j <= wa; j++){
                dp[i+1][j+keta[k]] = add(dp[i+1][j+keta[k]], mul(dp[i][j], i+1));
            }
        }
        wa += keta[k];
    }
    make();
    for(int i = 0; i < 205; i++){
        for(int j = 0; j <= i; j++){
            bar[i] = add(bar[i], mul(fac[i], finv[i-j]));
