        int ans1 = 0;
        for(int i = 0; i < n; i++){
            if(A[i] == 0) continue;
            for(int j = 0; j <= n; j++){
                for(int k = 0; k <= wa; k++){
                    dp2[j][k] = dp[j][k];
                }
            }
            for(int j = 0; j < n; j++){
                for(int k = 0; k <= wa-ket[i]; k++){
                    dp2[j+1][k+ket[i]] = sub(dp2[j+1][k+ket[i]], mul(dp2[j][k], j+1));
                }
            }
            for(int j = 0; j <= n-1; j++){
                for(int k = 0; k <= wa-ket[i]; k++){
                    ans1 = add(ans1, mul(mul(dp2[j][k], bar[n-j-1]), mul(A[i], zyu[k])));
