using namespace std;


// ------ 0. Variables, Functions ------ //

int n;

vector<int> fact;
vector<int> power2;
vector<int> power10;

int digit(int n)
{
	if (n < 10) return 1;

	return digit(n / 10) + 1;
}

int solve(vector<int> a)
{
	vector<vector<int> > d(5, vector<int>(2, 0));

	for (int i = 0; i < n; i++)
	{
		d[digit(a[i])][0] += 1;
		d[digit(a[i])][1] += a[i];
	}

	int ret = 0;

	for (int i = 1; i <= 4; i++)
	{
		vector<vector<vector<int> > > dp(5, vector<vector<int> >(n, vector<int>(n * 4, 0)));

		dp[0][0][0] = 1;

		if (d[i][0] != 0)
		{
			d[i][0]--;

			for (int j = 0; j < 4; j++)
			{
				for (int k = 0; k < n; k++)
				{
					for (int l = 0; l < n * 4; l++)
					{
						if (dp[j][k][l] != 0)
						{
							for (int m = 0; m <= d[j + 1][0]; m++)
							{
								dp[j + 1][k + m][l + (j + 1) * m]++;
								dp[j + 1][k + m][l + (j + 1) * m] %= mod;
							}
						}
					}
				}
			}

			d[i][0]++;

			int res = 0;

			for (int j = 0; j < n; j++)
			{
				for (int k = 0; k < n * 4; k++)
				{
					res = (res + 1LL * dp[4][j][k] * fact[j] % mod * power10[k] % mod * power2[n - j - 1] % mod) % mod;
				}
			}

			ret = (ret + 1LL * res * d[i][1]) % mod;
		}
	}

	return ret;
}

int main()
{
	// ------ 1. init ------ //

	fact.push_back(1);

	for (int i = 1; i <= 200; i++) fact.push_back(1LL * fact[i - 1] * i % mod);

	power2.push_back(1);

	for (int i = 1; i <= 200; i++) power2.push_back(1LL * power2[i - 1] * 2 % mod);

	power10.push_back(1);

	for (int i = 1; i <= 800; i++) power10.push_back(1LL * power10[i - 1] * 10 % mod);


	// ------ 2. Input ------ //

	scanf("%d", &n);

	vector<int> a(n);

	for (int i = 0; i < n; i++)
	{
		scanf("%d", &a[i]);
	}

	sort(a.begin(), a.end(), greater<int>());

	int res = solve(a);

	if (a[n - 1] == 0)
	{
		a.pop_back(); n--;

		res += (mod - solve(a));
		res %= mod;
	}

	printf("%d\n", res);

	return 0;
}

