	}

	rep(i,201)rep(j,i+1){
		if(j==0)
			P[i][j]=1LL;
		else
			P[i][j] = (i+1-j) * P[i][j-1] %mod;
	}

	ten[0] = 1LL;
	rep(i,1,801) ten[i] = 10LL * ten[i-1] %mod;
}

void func(int tar){
	if(num[tar]==0) return;
	num[tar]--;

	rep(i,210)rep(j,810) dp[i][j]=0LL;
	dp[0][0] = 1LL;

	rep(len,1,5){
		rep(loop,num[len]){
			rrep(j,n+1)rep(k,4*n+1){
				if(dp[j][k]==0) continue;
				dp[j+1][k+len] += 1LL * (j+1) * dp[j][k] % mod;	
				dp[j+1][k+len] %= mod;
			}
		}
	}

	coef[tar] = 0LL;

	const int m = n - 1 - num[0];

	rep(rnzero,m+1)rep(len,4*m+1){
		if(dp[rnzero][len]==0) continue;
		const int restnzero = m - rnzero;

		rep(lnzero,m-rnzero+1){
			rep(rzero,num[0]+1){
				rep(lzero,num[0]-rzero+1){

					ll cur = dp[rnzero][len];
					cur = 1LL * cur * ten[len + rzero] % mod;

					cur = 1LL * cur * P[restnzero][lnzero] % mod;
					cur = 1LL * cur * P[rnzero+rzero][rzero] % mod;

					if(lnzero == 0 and lzero == 0)
						cur = 1LL * cur;	
					else if(lnzero == 0  and lzero > 0)
						cur = 0;
					else
						cur = 1LL * cur * P[lnzero-1+lzero][lzero] % mod;

					//cerr << lnzero << " " << rnzero << " " << len << " " << lzero << " " << rzero << " " << cur << endl;

					coef[tar] = (coef[tar] + cur) % mod;
				}
			}
		}
	}
	num[tar]++;
}


