#include <bits/stdc++.h>
#define REP(i,n) for(int i=0; i<(int)(n); ++i)

using namespace std;
typedef long long ll;

const int    INF = 1000000000;
const ll     MOD = 1000000007;
const double EPS = 1e-8;

ll memo[210][810];
ll calc(int i, int k, ll dp[210][810], ll len){
    if(i == 0) return (k == 0 ? 1 : 0);
    if(k <  0) return 0;
    if(memo[i][k] != -1) return memo[i][k];
    return memo[i][k] = dp[i][k] - calc(i - 1, k - len, dp, len);
}

ll fact[210], ifact[210];
ll inv[210];
ll pow_10[210];
void init(int N){
    pow_10[0] = 1;
    for(int i = 1; i <= N; i++){
        pow_10[i] = pow_10[i - 1] * 10 % MOD;
    }
    inv[1] = 1;
    for (int i = 2; i <= N; ++i){
        inv[i] = inv[MOD % i] * (MOD - MOD / i) % MOD;
    }
    fact[0] = 1, ifact[0] = 1;
    for(int i = 1; i <= N; i++){
        fact[i] = fact[i - 1] * i % MOD;
        ifact[i] = ifact[i - 1] * inv[i] % MOD;
    }
}
ll C(int n, int r){
    if(n < 0 || r < 0 || r > n) return 0;
    if(r > n / 2) r = n - r;
    return fact[n] * ifact[n - r] % MOD * ifact[r] % MOD;
}
ll solve(vector<ll> a, vector<ll> len){
    int n = a.size();
    ll dp[210][810] = {};
    dp[0][0] = 1;
    for(int i = 0; i < n; i++){
        for(int j = i; j >= 0; j--){
            for(int k = 4 * i; k >= 0; k--){
                dp[j + 1][k + len[i]] += dp[j][k];
                dp[j + 1][k + len[i]] %= MOD;
            }
        }
    }

    ll ways_left[210] = {};
    for(int i = 0; i <= n; i++){
        for(int j = 0; j <= i; j++){
            ways_left[i] += C(i, j) * fact[j] % MOD;
        }
    }
    ll ans = 0;
    REP(i, n){
        REP(j, 210) REP(k, 810) memo[j][k] = -1;
        for(int j = 0; j <= n - 1; j++){
            for(int k = 0; k <= j * 4; k++){
                //printf("(%d, %d, %d) = %lld\n", i, j, k, ways_left[n - 1 - j] * a[i] % MOD * pow_10[k] % MOD * calc(j, k, dp, len[i]) % MOD * fact[j] % MOD);
                ans += ways_left[n - 1 - j] * a[i] % MOD * pow_10[k] % MOD * calc(j, k, dp, len[i]) % MOD * fact[j] % MOD;
                ans %= MOD;
            }
        }
    }
    return ans;
}

int main(){
    init(210);
    int n;
    while(cin >> n){
        vector<ll> a(n);
        vector<ll> len(n);
        vector<ll> b;
        vector<ll> len2;
        REP(i, n){
            string s;
            cin >> s;
            len[i] = s.size();
            a[i] = atoi(s.c_str());
            if(a[i] != 0){
                b.push_back(a[i]);
                len2.push_back(len[i]);
            }
        }
        if(a.size() == b.size()){
            cout << solve(a, len) << endl;
        }else{
            cout << (solve(a, len) - solve(b, len2) + MOD) % MOD << endl;
        }
    }
    return 0;
}

