using namespace std;
typedef long long ll;
const ll MD = 1e9+7;
const int MN = 210;
const int MM = 4;
const int MS = MN*MM;

int n;
string s[MN];
int si[MN];
int sd[MN];

ll inv2;
ll pow10[MS];
ll facdp[MN];

ll dp1[MN][MS], dp2[MN][MS];
ll calc() {
    inv2 = (MD+1)/2;
    pow10[0] = 1;
    for (int i = 1; i < MS; i++) {
        pow10[i] = pow10[i-1] * 10;
        pow10[i] %= MD;
    }

    for (int i = 0; i < MN; i++) {
        facdp[i] = 0;
        for (int j = 1; j <= i+1; j++) {
            ll sm = 1;
            for (int k = j; k <= i; k++) {
                sm *= k;
                sm %= MD;
            }
            facdp[i] += sm;
            facdp[i] %= MD;
        }
    }

    // for (int i = 0; i < 5; i++) {
    //     cout << facdp[i] << " ";
    // } cout << endl;
    for (int i = 0; i < n; i++) {
        si[i] = stoi(s[i]);
        sd[i] = (int)s[i].size();
    }
    memset(dp1, 0, sizeof(dp1));
    dp1[0][0] = 1;
    for (int fe = 0; fe < n; fe++) {
        int nsd = sd[fe];
        for (int i = 0; i < MN; i++) {
            for (int j = 0; j < MS; j++) {
                dp2[i][j] = dp1[i][j];
                if (i >= 1 && j >= nsd) {
                    dp2[i][j] += i*dp1[i-1][j-nsd];
                    dp2[i][j] %= MD;
                }
            }
        }
        memcpy(dp1, dp2, sizeof(dp1));
    }

    // for (int i = 0; i <= n; i++) {
    //     for (int j = 0; j < 10; j++) {
    //         cout << dp1[i][j] << " ";
    //     } cout << endl;
    // }
    ll sm = 0;
    for (int fe = 0; fe < n; fe++) {
        int nsd = sd[fe];
        for (int i = 0; i < MN; i++) {
            for (int j = 0; j < MS; j++) {
                dp2[i][j] = dp1[i][j];
                if (i >= 1 && j >= nsd) {
                    dp2[i][j] -= i*dp2[i-1][j-nsd];
                    dp2[i][j] = (dp2[i][j]%MD+MD) % MD;
                }
                sm += pow10[j] * dp2[i][j] %MD * si[fe] %MD * facdp[n-i-1] %MD;
                sm %= MD;
            }
        }
    }
    return sm;
}

int main() {
    cin >> n;
    for (int i = 0; i < n; i++) {
        cin >> s[i];
    }
    sort(s, s+n, [&](const string &l, const string &r){
        return stoi(l) < stoi(r);
    });

    ll u = calc();
    if (s[0] == "0") {
        reverse(s, s+n);
        reverse(s, s+n-1);
        n--;
        u -= calc();
        u = (u+MD) % MD;
    }
    cout << u << endl;
    return 0;
}

#include <bits/stdc++.h>
#define REP(i,n) for(int i=0; i<(int)(n); ++i)

using namespace std;
typedef long long ll;

const int    INF = 1000000000;
const ll     MOD = 1000000007;
const double EPS = 1e-8;

ll memo[210][810];
ll calc(int i, int k, ll dp[210][810], ll len){
    if(i == 0) return (k == 0 ? 1 : 0);
    if(k <  0) return 0;
    if(memo[i][k] != -1) return memo[i][k];
    return memo[i][k] = (dp[i][k] - calc(i - 1, k - len, dp, len + MOD)) % MOD;
}

ll fact[210], ifact[210];
ll inv[210];
ll pow_10[210];
void init(int N){
    pow_10[0] = 1;
    for(int i = 1; i <= N; i++){
        pow_10[i] = pow_10[i - 1] * 10 % MOD;
    }
    inv[1] = 1;
    for (int i = 2; i <= N; ++i){
        inv[i] = inv[MOD % i] * (MOD - MOD / i) % MOD;
    }
    fact[0] = 1, ifact[0] = 1;
    for(int i = 1; i <= N; i++){
        fact[i] = fact[i - 1] * i % MOD;
        ifact[i] = ifact[i - 1] * inv[i] % MOD;
    }
}
ll C(int n, int r){
    if(n < 0 || r < 0 || r > n) return 0;
    if(r > n / 2) r = n - r;
    return fact[n] * ifact[n - r] % MOD * ifact[r] % MOD;
}
ll solve(vector<ll> a, vector<ll> len){
    int n = a.size();
    ll dp[210][810] = {};
    dp[0][0] = 1;
    for(int i = 0; i < n; i++){
        for(int j = i; j >= 0; j--){
            for(int k = 4 * i; k >= 0; k--){
                dp[j + 1][k + len[i]] += dp[j][k];
                dp[j + 1][k + len[i]] %= MOD;
            }
        }
    }

    ll ways_left[210] = {};
    for(int i = 0; i <= n; i++){
        for(int j = 0; j <= i; j++){
            ways_left[i] += C(i, j) * fact[j] % MOD;
            ways_left[i] %= MOD;
        }
    }
    ll ans = 0;
    REP(i, n){
        REP(j, 210) REP(k, 810) memo[j][k] = -1;
        for(int j = 0; j <= n - 1; j++){
            for(int k = 0; k <= j * 4; k++){
                //printf("(%d, %d, %d) = %lld\n", i, j, k, ways_left[n - 1 - j] * a[i] % MOD * pow_10[k] % MOD * calc(j, k, dp, len[i]) % MOD * fact[j] % MOD);
                ans += ways_left[n - 1 - j] * a[i] % MOD * pow_10[k] % MOD * calc(j, k, dp, len[i]) % MOD * fact[j] % MOD;
                ans %= MOD;
            }
        }
    }
    return ans;
}

int main(){
    init(210);
    int n;
    while(cin >> n){
        vector<ll> a(n);
        vector<ll> len(n);
        vector<ll> b;
        vector<ll> len2;
        REP(i, n){
            string s;
            cin >> s;
            len[i] = s.size();
            a[i] = atoi(s.c_str());
            if(a[i] != 0){
                b.push_back(a[i]);
                len2.push_back(len[i]);
            }
        }
        if(a.size() == b.size()){
            cout << solve(a, len) << endl;
        }else{
            cout << (solve(a, len) - solve(b, len2) + 2 * MOD) % MOD << endl;
        }
    }
    return 0;
}

#ifndef ___CLASS_MODINT
#define ___CLASS_MODINT

#include <cstdint>

template<std::uint32_t mod>
class modint {
private:
	std::uint32_t n;
public:
	modint() : n(0) {};
	modint(std::uint64_t n_) : n(n_ % mod) {};
	bool operator==(const modint& m) const { return n == m.n; }
	bool operator!=(const modint& m) const { return n != m.n; }
	std::uint32_t get() const { return n; }
	modint& operator+=(const modint& m) { n += m.n; n = (n < mod ? n : n - mod); return *this; }
	modint& operator-=(const modint& m) { n += mod - m.n; n = (n < mod ? n : n - mod); return *this; }
	modint& operator*=(const modint& m) { n = std::uint64_t(n) * m.n % mod; return *this; }
	modint operator+(const modint& m) const { return modint(*this) += m; }
	modint operator-(const modint& m) const { return modint(*this) -= m; }
	modint operator*(const modint& m) const { return modint(*this) *= m; }
	modint binpow(std::uint64_t b) const {
		modint ans = 1, m = modint(*this);
		while (b) {
			if (b & 1) ans *= m;
			m *= m;
			b >>= 1;
		}
		return ans;
	}
	modint inv() { return (*this).binpow(mod - 2); }
};

#endif // ___CLASS_MODINT

#include <vector>
#include <iostream>
using namespace std;
using modulo = modint<1000000007>;
modulo solve(vector<int> A) {
	int N = A.size();
	vector<int> D(N);
	for (int i = 0; i < N; ++i) {
		int x = A[i];
		while (x > 0) {
			x /= 10;
			++D[i];
		}
		if (A[i] == 0) D[i] = 1;
	}
	int sum = 0;
	for (int i = 0; i < N; ++i) sum += D[i];
	vector<modulo> pw(sum + 1); pw[0] = 1;
	for (int i = 1; i <= sum; ++i) pw[i] = pw[i - 1] * 10;
	vector<vector<modulo> > dp(N + 1);
	dp[0] = { modulo(1) };
	for (int i = 0; i < N; ++i) {
		dp[i + 1].resize(i + 2);
		for (int j = 0; j <= i; ++j) {
			dp[i + 1][j] += dp[i][j];
			dp[i + 1][j + 1] += dp[i][j] * pw[D[i]];
		}
	}
	vector<vector<modulo> > rdp(N + 1);
	rdp[N] = { modulo(1) };
	for (int i = N - 1; i >= 0; --i) {
		rdp[i].resize(N - i + 1);
		for (int j = 0; j <= N - i - 1; ++j) {
			rdp[i][j] += rdp[i + 1][j];
			rdp[i][j + 1] += rdp[i + 1][j] * pw[D[i]];
		}
	}
	vector<vector<modulo> > comb(N + 1);
	for (int i = 0; i <= N; ++i) {
		comb[i].resize(i + 1);
		comb[i][0] = 1;
		for (int j = 1; j <= i; ++j) {
			comb[i][j] += comb[i - 1][j - 1];
			if (j != i) comb[i][j] += comb[i - 1][j];
		}
	}
	vector<modulo> fact(N + 1);
	vector<modulo> sumfact(N + 1);
	fact[0] = 1;
	sumfact[0] = 1;
	for (int i = 1; i <= N; ++i) {
		fact[i] = fact[i - 1] * i;
		for (int j = 0; j <= i; ++j) {
			sumfact[i] += fact[j] * comb[i][j];
		}
	}
	modulo ans = 0;
	for (int i = 0; i < N; ++i) {
		for (int j = 0; j <= i; ++j) {
			for (int k = 0; k <= N - i - 1; ++k) {
				modulo sub = dp[i][j] * rdp[i + 1][k];
				ans += sub * A[i] * fact[j + k] * sumfact[N - j - k - 1];
			}
		}
	}
	return ans;
}
int main() {
	int N;
	cin >> N;
	vector<int> A(N), ANZ;
	for (int i = 0; i < N; ++i) {
		cin >> A[i];
		if (A[i] != 0) ANZ.push_back(A[i]);
	}
	modulo ans = solve(A);
	if (A != ANZ) ans -= solve(ANZ);
	cout << ans.get() << endl;
	return 0;
}

#include <cstdio>
#include <cstdint>
#include <algorithm>
#include <vector>
#include <utility>
#include <numeric>

constexpr intmax_t mod = 1e9+7;

template <class Tp>
Tp gcd(Tp a, Tp b, Tp& x, Tp& y) {
  x = Tp(0);
  y = Tp(1);
  for (Tp u=y, v=x; a;) {
    Tp q = b/a;
    std::swap(x-=q*u, u);
    std::swap(y-=q*v, v);
    std::swap(b-=q*a, a);
  }
  return b;
}

template <class Tp>
Tp modinv(Tp a, Tp mod) {
  Tp x, y;
  gcd(a, mod, x, y);
  x %= mod;
  if (x < 0) x += mod;
  return x;
}

template <class Tp>
Tp modadd(Tp a, Tp b, Tp mod) {
  a += b % mod;
  if (a < 0) a += mod;
  if (a >= mod) a -= mod;
  return a;
}

template <class Tp>
Tp modadd(const std::initializer_list<Tp>& adds, Tp mod) {
  Tp res = 0;
  for (const auto& add: adds) {
    res += add % mod;
    if (res < 0) res += mod;
    if (res >= mod) res -= mod;
  }
  return res;
}

template <class Tp>
Tp modsub(Tp a, Tp b, Tp mod) {
  a -= b % mod;
  if (a < 0) a += mod;
  if (a >= mod) a -= mod;
  return a;
}

template <class Tp>
Tp modmul(const std::initializer_list<Tp>& muls, Tp mod) {
  Tp res = 1;
  for (const auto& mul: muls) (res *= mul) %= mod;
  return res;
}

template <class Tp>
Tp modpow(Tp base, intmax_t iexp, Tp mod) {
  Tp res = 1;
  for (Tp dbl = base; iexp; iexp >>= 1) {
    if (iexp & 1) res = res * dbl % mod;
    dbl = dbl * dbl % mod;
  }
  return res;
}

class modfactorial {
  std::vector<intmax_t> fact, fact_inv;
  intmax_t mod;

public:
  modfactorial(intmax_t N, intmax_t mod): mod(mod) {
    fact.resize(N+1);
    fact_inv.resize(N+1);
    fact[0] = 1;
    for (intmax_t i = 1; i <= N; ++i)
      fact[i] = fact[i-1] * i % mod;

    fact_inv[N] = modinv(fact[N], mod);
    for (intmax_t i = N; i--;)
      fact_inv[i] = fact_inv[i+1] * (i+1) % mod;
  }

  intmax_t operator ()(intmax_t k) const {
    return fact[k];
  }

  intmax_t inverse(intmax_t k) const {
    return fact_inv[k];
  }
};

template <class Tp>
std::vector<Tp> make_vector(size_t n, const Tp& x) {
  return std::vector<Tp>(n, x);
}

template <class... Ts>
auto make_vector(size_t n, size_t m, const Ts&... x) {
  return std::vector<decltype(make_vector(m, x...))>(n, make_vector(m, x...));
}

std::vector<std::vector<intmax_t>> make_dp(std::vector<size_t> dig, size_t d) {
  --dig[d];
  size_t n = std::accumulate(dig.begin(), dig.end(), 0);
  size_t m = 0;
  for (size_t i = 1; i <= 4; ++i) m += i * dig[i];
  auto dp = make_vector(n+1, n+1, m+1, intmax_t(0));
  dp[0][0][0] = 1;

  std::vector<size_t> ds;
  ds.reserve(n);
  for (size_t i = 1; i <= 4; ++i)
    for (size_t j = 0; j < dig[i]; ++j)
      ds.push_back(i);

  for (size_t i = 0; i < n; ++i) {
    for (size_t j = 0; j < n; ++j) {
      for (size_t k = 0; k <= m; ++k) {
        (dp[i+1][j][k] += dp[i][j][k]) %= mod;
        if (k+ds[i] <= m) {
          (dp[i+1][j+1][k+ds[i]] += (j+1) * dp[i][j][k]) %= mod;
        }
      }
    }
  }

  return dp[n];
}

intmax_t solve(const std::vector<intmax_t>& a) {
  size_t n = a.size();
  std::vector<size_t> dig(5);
  std::vector<intmax_t> sum(5);
  for (auto ai: a) {
    if (ai == 0) {
      ++dig[1];
      continue;
    }
    int d = 0;
    for (int i = ai; i > 0; i /= 10) ++d;
    ++dig[d];
    (sum[d] += ai) %= mod;
  }

  modfactorial mf(n+1, mod);
  auto coes = make_vector(n, n, intmax_t(0));
  for (size_t j = 0; j < n; ++j)
    for (size_t l = 0; l+j < n; ++l)
      coes[j][l] = mf(n-1-j) * mf.inverse(n-1-j-l) % mod;

  intmax_t res = 0;
  for (int i = 1; i <= 4; ++i) {
    if (!dig[i]) continue;
    auto dp = make_dp(dig, i);

    for (size_t j = 0; j < n; ++j) {
      for (size_t l = 0; l+j < n; ++l) {
        intmax_t cur = 0;
        for (size_t k = 0; k < dp[j].size(); ++k) {
          cur += modmul({sum[i], modpow(10L, k, mod), dp[j][k]}, mod);
          if (cur >= mod) cur -= mod;
        }
        (res += cur * coes[j][l]) %= mod;
      }
    }
  }
  return res;
}

int main() {
  size_t n;
  scanf("%zu", &n);

  std::vector<intmax_t> a(n);
  for (auto& ai: a) scanf("%jd", &ai);

  std::sort(a.rbegin(), a.rend());
  intmax_t res = solve(a);
  if (a[n-1] == 0) {
    a.pop_back();
    res -= solve(a);
