int ncr(int n,int r)
{
	static int memo[300][300];
	if(memo[n][r]) return memo[n][r];
	if(r==0 || r==n) return 1;
	return memo[n][r]=(ncr(n-1,r-1)+ncr(n-1,r))%MOD;
}

ll solve(vi as)
{
	vi tinvs(1000,1); // tinvs[i]=10^i mod m
	repi(i,1,tinvs.size()) tinvs[i]=(ll)tinvs[i-1]*10%MOD;
	vi finvs(200,1); // finvs[i]=i! mod m
	repi(i,1,finvs.size()) finvs[i]=(ll)finvs[i-1]*i%MOD;

	int n=as.size();
	vi ls(n);
	rep(i,n){
		if(as[i]==0) ls[i]=1;
		for(int x=as[i];x;x/=10) ls[i]++;
	}

	ll res=0;
	rep(i,n){
		vvl dp(n+1,vl(4*n+1)); // dp[][]=
		dp[0][0]=1;
		rep(j,n) if(j!=i){
			vvl dp2=dp;
			rep(lo,dp.size()) rep(len,dp[lo].size()) if(dp2[lo][len])
				(dp[lo+1][len+ls[j]]+=dp2[lo][len])%=MOD;
		}

		ll tmp=0;
		rep(lo,dp.size()) rep(len,dp[lo].size()) if(dp[lo][len]){
			rep(hi,n-lo){
				ll val=(ll)as[i]*tinvs[len]%MOD*dp[lo][len]%MOD;
				ll locnt=finvs[lo];
				ll hicnt=(ll)ncr(n-1-lo,hi)*finvs[hi]%MOD;
				(tmp+=val*locnt%MOD*hicnt%MOD)%=MOD;
			}
		}
		(res+=tmp)%=MOD;
	}
	return res;
}

