long long solve(vector<int> vec){
	long long res=0;
	for(int i=0;i<5;i++) checked[i]=false;
	int N=vec.size();
	for(int i=0;i<N;i++){
		int k=getDig(vec[i]);
		int t=0;
		if(checked[k]) goto end_dp;
		checked[k]=true;
		digs.clear();
		for(int j=0;j<N;j++){
			if(i==j) continue;
			digs.push_back(getDig(vec[j]));
		}
		for(int a=0;a<=200;a++) for(int b=0;b<=800;b++) dp[0][a][b]=0;
		dp[0][0][0]=1;
		t=0;
		for(int j=0;j<N-1;j++){
			for(int a=0;a<=200;a++) for(int b=0;b<=800;b++) dp[1-t][a][b]=0;
			for(int a=0;a<=200;a++) for(int b=0;b<=800;b++){
				dp[1-t][a][b]+=dp[t][a][b];
				if(a+1<=200&&b+digs[j]<=800){
					dp[1-t][a+1][b+digs[j]]+=dp[t][a][b];
				}
			}
			for(int a=0;a<=200;a++) for(int b=0;b<=800;b++){
				dp[1-t][a][b]%=mod;
			}
			t=1-t;
		}
		for(int a=0;a<=200;a++) for(int b=0;b<=800;b++) dp2[k][a][b]=dp[t][a][b];
		end_dp:;
		for(int a=0;a<=200;a++) for(int b=0;b<=800;b++){
			int c=N-1-a;
			long long coe1=sum[c];
			long long coe2=pw[b];
			//long long coe3=dp[t][a][b];
			long long coe3=dp2[k][a][b];
			long long tmp=(coe1*coe2)%mod;
			tmp*=coe3;
			tmp%=mod;
			tmp*=fact[a];
			tmp%=mod;
			tmp*=vec[i];
			tmp%=mod;
			res=(res+tmp)%mod;
		}
	}
	return res;
}

