int solve(vi& vec,vi&d,int n){
    rep(j,n+1){
        rep(k,801){
            dp[j][k] = 0;
        }
    }
    dp[0][0] = 1;
    rep(i,n){
        rrep(j,i+1){
            rep(k,801){
                if(dp[j][k]){
                    dp[j+1][k+d[i]] = add(dp[j+1][k+d[i]],dp[j][k]);
                }
            }
        }
    }
    ll ans = 0;
    rep(i,n){
        if(vec[i]){
            rep(j,n+1){
                rep(k,801){
                    dp2[j][k] = dp[j][k];
                }
            }
            rep(j,n){
                srep(k,d[i],801){
                    if(dp2[j][k-d[i]]){
                        dp2[j+1][k] = sub(dp2[j+1][k],dp2[j][k-d[i]]);
                    }
                }
            }
            rep(j,n){
                rep(k,801){
                    if(dp2[j][k]){
                        ans = add(ans,mul(mul(mul(dp2[j][k],smpow[n-j]),mul(ten[k],vec[i])),fac[j]));
                    }
                }
            }
        }
    }
    return ans;
}

