int calcdigit(int v){
	int ret=0;
	if(v==0)return 1;
	while(v>0){
		v/=10;
		ret++;
	}
	return ret;
}

int main(void){
	scanf("%d",&n);
	for(int i=0;i<n;i++){
		scanf("%lld",&a[i]);
	}
	ten[0]=1;
	for(int i=0;i<=10*n;i++){
		ten[i+1]=ten[i]*10LL%MOD;
	}
