					if(dp[prev][j][k][l]>0){
						if(l==0){
							dp[now][j][k][l]+=dp[prev][j][k][l]%MOD;
							dp[now][j][k][l]%=MOD;

							if(a[i]!=0){
								dp[now][j+1][k][l]+=dp[prev][j][k][l]*(j+1)%MOD;
								dp[now][j+1][k][l]%=MOD;
							}else{
								dp[now][j+1][k][l]+=dp[prev][j][k][l]*j%MOD;
								dp[now][j+1][k][l]%=MOD;
							}


							dp[now][j][k+1][l]+=dp[prev][j][k][l]*(k+1)*ten[dig]%MOD;
							dp[now][j][k+1][l]%=MOD;

							dp[now][j][k][1]+=dp[prev][j][k][l]*a[i]%MOD;
							dp[now][j][k][1]%=MOD;
						}else{
							dp[now][j][k][l]+=dp[prev][j][k][l]%MOD;
							dp[now][j][k][l]%=MOD;

							if(a[i]!=0){
								dp[now][j+1][k][l]+=dp[prev][j][k][l]*(j+1)%MOD;
								dp[now][j+1][k][l]%=MOD;
