		}

		ans2=(ans2+res*digitsum[i])%INF;
		//cout<<i<<" : "<<digitsum[i]<<" * "<<res<<endl;

		m[i]++;
		D+=i;
	}

	//cout<<ans<<" - "<<ans2<<" = \n";
	cout<<(ans-ans2+INF)%INF<<endl;
}

