int N;
void culc_dp(int unuse)
{
	memset(dp,0,sizeof(dp));
	dp[0][0]=1;
	for(int i=0;i<N;i++)
	{
		if(i==unuse)continue;
		for(int j=i;j>=0;j--)
		{
			for(int k=0;k<=j*4;k++)
			{
				if(dp[j][k]==0)continue;
				dp[j+1][k+dig[i]]+=dp[j][k];
			}
		}
	}
}
ll solve()
{
	ll ans = 0ll;
	for(int d=1;d<=4;d++)
	{
		if(del[d]==-1)continue;
		culc_dp(del[d]);
		for(int i=0;i<N;i++)
		{
			for(int j=0;j<=4*N;j++)
			{
				if(dp[i][j]==0)continue;
				ans += (((((((val[N-i-1]*kai[i])%MOD)*digit_sum[d])%MOD)*dp[i][j])%MOD)*Pow[j])%MOD;
				ans %= MOD;
			}
		}
	}
	return ans;
}

