void calc_dp(int n,const int *len,ll dp[200][800]){
	dp[0][0]=1;
	rep(i,n) for(int k=n-1;k>=0;k--) for(int j=4*n-len[i];j>=0;j--) {
		dp[k+1][j+len[i]]=(dp[k+1][j+len[i]]+dp[k][j])%M;
	}
	rep(i,n+1) rep(j,4*n) dp[i][j]=dp[i][j]*fact[i]%M; // 
}

ll solve(int n,const int *a){
	int len[200];
	rep(i,n){
		char s[8]; sprintf(s,"%d",a[i]);
		len[i]=strlen(s);
	}

	ll cnt[200]={}; // cnt[i] := ( i  )
	rep(i,n) rep(j,i+1) cnt[i]=(cnt[i]+nCr[i][j]*fact[j])%M;

	// dp[i][j][k] := ( a[0..n-1]  i+1 , j  k  )
	static ll dp[4][200][800];
	rep(i,4) rep(j,n) rep(k,4*n) dp[i][j][k]=0;
	rep(i,4){
		int pos=-1;
		rep(j,n) if(len[j]-1==i) pos=j;
		if(pos!=-1){
			swap(len[pos],len[n-1]);
			calc_dp(n-1,len,dp[i]);
			swap(len[pos],len[n-1]);
		}
	}

	ll ans=0;
	rep(i,n){
		ll ten=1;
		rep(j,4*n){
			rep(k,n){
				ans+=cnt[n-k-1]*dp[len[i]-1][k][j]%M*a[i]%M*ten%M;
				ans%=M;
			}
			ten=ten*10%M;
		}
	}
	return ans;
}

