vector<vector<ll> > digits_dp(const vector<int>& digits)
{
    int n = digits.size();

    static ll dp[210][M + 30];
    CL(dp, 0);
    dp[0][0] = 1;
    for (int i = 0; i < n; ++i)
        for (int j = i; j >= 0; --j)
            for (int k = M - 1; k >= 0; --k)
                add(dp[j + 1][k + digits[i]], dp[j][k]);

    vector<vector<ll> > res(n + 1, vector<ll>(M));
    for (int i = 0; i <= n; ++i)
        for (int j = 0; j < M; ++j)
            res[i][j] = dp[i][j];
    return res;
}
ll gao(const vector<int>& a, const vector<int>& digits)
{
    int n = a.size();

    ll pat[210] = {};
    for (int i = 0; i <= n - 1; ++i)
        for (int j = 0; j <= (n - 1) - i; ++j)
            (pat[i] += (combi[(n - 1) - i][j] * fact[j] % mod) * fact[i]) %= mod;

    vector<vector<ll> > digits_pat[6];
    for (int i = 1; i <= 5; ++i)
    {
        bool f = false;
        vector<int> d;
        for (int j = 0; j < n; ++j)
        {
            if (i != digits[j] || f)
                d.push_back(digits[j]);
            else
                f = true;
        }

        if (f)
            digits_pat[i] = digits_dp(d);
    }

    ll res = 0;
    for (int i = 0; i < n; ++i)
    {
        vector<vector<ll> >& d_pat = digits_pat[digits[i]];

        for (int j = 0; j <= n - 1; ++j)
        {
            for (int k = 0; k < M; ++k)
            {
                ll p = pat[j] * d_pat[j][k] % mod;
                (res += ((a[i] * ten_pow[k]) % mod) * p) %= mod;
            }
        }
    }

    return res;
}
