          //
          dp[j+1][k] = (dp[j+1][k] + dp[j][k] * (j+1)) % mod;
          //
          dp[j][k+1] = (dp[j][k+1] + dp[j][k] * log10a[d] % mod * (k+1)) % mod;
          //
          ;
        }
