	dp[0][0]=1;
	vl S(n);
	for(int i=0;i<n;i++) S[i]=to_string(a[i]).size();
	for(int i=0;i<n;i++){
		for(int j=n;j>0;j--) for(int k=N-1;k>=S[i];k--) (dp[j][k]+=dp[j-1][k-S[i]])%=mod;
	}
	for(int i=0;i<n;i++){
		DP=dp;
		for(int j=1;j<=n;j++) for(int k=S[i];k<N;k++){
			DP[j][k]=(DP[j][k]-DP[j-1][k-S[i]]+mod)%mod;
		}
		for(int j=0;j<n;j++){
			ll ten=1,re=1;
			for(int k=1;k<n-j;k++) (re+=nPk(n-j-1,k))%=mod;
			for(int k=0;k<N;k++){
				(res+=DP[j][k]*ten%mod*a[i]%mod*re%mod*Fact(j))%=mod;
				(ten*=10)%=mod;
			}
		}
	}
	return res;
}

